$(".transition-btn").on("click",function(){var n=$(this).attr("data-transition");$(this).parent().children(".transition-element").transition(n)}),$(".transition-btn-delayed").on("click",function(){var n=$(this).data("transition");$(".transition-element-delayed").transition({animation:n,reverse:"auto",duration:800,interval:200,onComplete:function(){$(".transition-element-delayed").removeClass("hidden")}})});